import HexReader from 'dimensions/packetsold/hexreader';
import Color from 'dimensions/color';
import { a2hex } from 'dimensions/utils';

/**
 * Takes out the type and packet length from packet and acts the same as a HexReader
 */
class PacketReader extends HexReader {
    protected _type: number;

    constructor(data: string) {
        // Store data after length and type
        super(data.substr(6));
        this._type = parseInt(data.substr(4, 2), 16);
    }

    /**
     * Gets the packet type
     * 
     * @return The packet type of the packet
     */
    public get type(): number {
        return this._type;
    }
}

export default PacketReader;